<header
  class="bg-white shadow-[0_1px_2px_rgba(0,0,0,0.15)] relative z-40 px-6 lg:px-8 py-4 flex items-center justify-between"
  role="banner"
>
  <!-- Left Section -->
  <div class="head flex gap-0 md:gap-4 lg:gap-2 items-center">
    
    <!-- Menu Button -->
    <button
      type="button"
      onclick="openMenu()"
      class="h-10 w-10 flex items-center justify-center focus:outline-none focus:ring-2 focus:ring-primary-color rounded"
      aria-label="Open sidebar menu"
    >
      <img
        src="assets/images/leftmenu.svg"
        class="h-5 w-5"
        alt=""
        aria-hidden="true"
      />
    </button>

    <h1 class="text-[1.6rem] md:block hidden font-bold">
      Daily Operations Dashboard
    </h1>
  </div>

  <!-- Right Section -->
  <div class="flex items-center gap-4">
    
    <!-- Notification Button -->
    <button
      type="button"
      class="relative w-10 h-10 flex items-center justify-center focus:outline-none focus:ring-2 focus:ring-primary-color rounded"
      aria-label="View notifications (2 unread)"
    >
      <svg width="22" height="24" viewBox="0 0 22 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
