      </svg>

      <!-- Red Badge with "2" -->
      <span class="absolute -top-1 -right-1 flex items-center justify-center w-5 h-5 text-xs font-bold text-white bg-red-600 rounded-full ring-2 ring-white">
        2
      </span>
    </button>

    <!-- Profile Dropdown -->
    <div class="relative">
      <!-- Profile Trigger Button -->
      <button
        id="profile-trigger"
        type="button"
        onclick="toggleDropdown()"
        class="flex items-center gap-3 focus:outline-none focus:ring-2 focus:ring-primary-color rounded-lg"
        aria-haspopup="true"
        aria-expanded="false"
        aria-label="Open user account menu"
      >
        <div class="w-10 h-10 rounded-full overflow-hidden flex-shrink-0">
          <img
            src="assets/images/profile.png"
            class="h-full w-full object-cover"
            alt="User profile picture"
          />
        </div>
      </button>

      <!-- Dropdown Menu -->
      <div
        id="profile-dropdown"
        class="absolute right-0 mt-2 w-56 rounded-[25px] origin-top-right bg-white overflow-hidden divide-y  divide-gray-100 shadow-lg opacity-0 invisible transition-all shadow-[0px_4px_64px_0px_#00000029] duration-200 z-50"
        role="menu"
        aria-labelledby="profile-trigger"
      >
        <!-- User Header -->
        <div class="py-[12px] px-[15px]" role="none">
          <p class="text-sm font-medium text-gray-900" role="none">Demo User</p>
          <p class="text-sm text-gray-500 truncate" role="none">demo@example.com</p>
        </div>

        <!-- Menu Items -->
        <ul class="py-1" role="none">
          <li>
            <a
              href="#"
              role="menuitem"
              class="flex items-center gap-3 px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 focus:bg-gray-100 focus:outline-none"
            >
              <svg class="w-5 h-5 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"></path>
              </svg>
              Profile
            </a>
          </li>
          <li>
            <a
              href="#"
              role="menuitem"
              class="flex items-center gap-3 px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 focus:bg-gray-100 focus:outline-none"
            >
